// BirthdayInvitation.js

import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { Button, Checkbox, FormControlLabel, TextField, Typography, Box, FormGroup } from '@mui/material';
import emailjs from 'emailjs-com';

const alcoholOptions = [
  "Вино",
  "Пиво",
  "Виски",
  "Водка",
  "Ром",
  "Текила"
];

const BirthdayInvitation = () => {
  const formik = useFormik({
    initialValues: {
      name: '',
      willAttend: false,
      alcoholSelection: [],
    },
    validationSchema: Yup.object({
      name: Yup.string().required('Пожалуйста введите свое имя'),
      willAttend: Yup.boolean(),
      alcoholSelection: Yup.array(),
    }),
    onSubmit: (values, { resetForm }) => {
      emailjs.send("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", {
        name: values.name,
        willAttend: values.willAttend ? "Yes" : "No",
        alcoholSelection: values.alcoholSelection.join(", "),
      }, "YOUR_USER_ID")
      .then((response) => {
        alert("Данные отправлены успешно!");
        resetForm();
      })
      .catch((error) => {
        alert("Ошибка при отправке данных, Попробуйте сново.");
      });
    },
  });

  return (
    <Box component="form" onSubmit={formik.handleSubmit} sx={{ maxWidth: 400, margin: 'auto', padding: 4 }}>
      <Typography variant="h4" gutterBottom>
        Приглашение на день рождение рината
      </Typography>
      <TextField
        fullWidth
        label="Name"
        name="name"
        value={formik.values.name}
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        error={formik.touched.name && Boolean(formik.errors.name)}
        helperText={formik.touched.name && formik.errors.name}
        margin="normal"
      />
      <FormControlLabel
        control={
          <Checkbox
            name="willAttend"
            checked={formik.values.willAttend}
            onChange={formik.handleChange}
          />
        }
        label="Вы придете?"
      />
      <Typography variant="h6" gutterBottom>
        Какой алкоголь вы пьете:
      </Typography>
      <FormGroup>
        {alcoholOptions.map((option) => (
          <FormControlLabel
            key={option}
            control={
              <Checkbox
                name="alcoholSelection"
                value={option}
                checked={formik.values.alcoholSelection.includes(option)}
                onChange={formik.handleChange}
              />
            }
            label={option}
          />
        ))}
      </FormGroup>
      <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
        Отправить
      </Button>
    </Box>
  );
};

export default BirthdayInvitation;
