import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { Box, TextField, Checkbox, FormControlLabel, Button } from '@mui/material';

const BirthdayInvitationForm = () => {
  const [name, setName] = useState('');
  const [attending, setAttending] = useState(false);
  const [drinks, setDrinks] = useState([]);

  const handleDrinkChange = (event) => {
    const { value, checked } = event.target;
    setDrinks(prevDrinks =>
      checked ? [...prevDrinks, value] : prevDrinks.filter(drink => drink !== value)
    );
  };

  const sendEmail = (e) => {
    e.preventDefault();

    const templateParams = {
      name: name,
      attending: attending ? 'Да' : 'Нет',
      drinks: drinks.join(', '),
    };

    emailjs.send('service_qqiw1rh', 'template_ndcfuje', templateParams, 'KCn971jYkZdaYlyjl')
      .then((response) => {
        console.log('SUCCESS!', response.status, response.text);
        // Здесь можно добавить уведомление о успешной отправке
      }, (err) => {
        console.log('FAILED...', err);
        // Здесь можно добавить уведомление об ошибке
      });
  };

  return (
    <Box component="form" onSubmit={sendEmail} sx={{ maxWidth: '400px', margin: 'auto' }}>
      <TextField 
        fullWidth 
        label="Ваше имя" 
        variant="outlined" 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        margin="normal"
      />
      <FormControlLabel
        control={
          <Checkbox 
            checked={attending} 
            onChange={() => setAttending(!attending)} 
          />
        }
        label="Иду на день рождения"
      />
      <div>
        <FormControlLabel
          control={
            <Checkbox 
              value="Вино" 
              onChange={handleDrinkChange} 
            />
          }
          label="Вино"
        />
        <FormControlLabel
          control={
            <Checkbox 
              value="Пиво" 
              onChange={handleDrinkChange} 
            />
          }
          label="Пиво"
        />
        <FormControlLabel
          control={
            <Checkbox 
              value="Водка" 
              onChange={handleDrinkChange} 
            />
          }
          label="Водка"
        />
      </div>
      <Button type="submit" variant="contained" color="primary" sx={{ marginTop: '20px' }}>
        Отправить приглашение
      </Button>
    </Box>
  );
};

export default BirthdayInvitationForm;
