import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { Box, TextField, Checkbox, FormControlLabel, Button, Typography, FormGroup, Alert } from '@mui/material';
import { Formik, Field, Form } from 'formik';
import * as Yup from 'yup';

const BirthdayInvitationForm = () => {
  const [alertMessage, setAlertMessage] = useState('');

  const validationSchema = Yup.object({
    name: Yup.string()
      .min(2, 'Имя должно содержать минимум 2 символа')
      .required('Это поле обязательно'),
    attending: Yup.boolean().required('Вы должны указать, придете ли вы'),
  });

  const sendEmail = (values) => {
    const templateParams = {
      name: values.name,
      attending: values.attending ? 'Да' : 'Нет',
      drinks: values.drinks.join(', '),
    };

    emailjs.send('service_qqiw1rh', 'template_ndcfuje', templateParams, 'KCn971jYkZdaYlyjl')
      .then((response) => {
        console.log('SUCCESS!', response.status, response.text);
        setAlertMessage('Приглашение успешно отправлено!');
      }, (err) => {
        console.log('FAILED...', err);
        setAlertMessage('Произошла ошибка при отправке');
      });
  };

  return (
    <Box
      component="div"
      sx={{
        maxWidth: '400px',
        margin: 'auto',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        color: "white",
      }}
    >
      <Typography variant="h5" align="center" sx={{ marginBottom: '20px' }}>
        Приглашение на день рождения
      </Typography>

      <Formik
        initialValues={{
          name: '',
          attending: false,
          drinks: [],
        }}
        validationSchema={validationSchema}
        onSubmit={sendEmail}
      >
        {({ setFieldValue, values, errors, touched }) => (
          <Form>
            <Field
              as={TextField}
              fullWidth
              label="Ваше имя"
              variant="outlined"
              name="name"
              margin="normal"
              value={values.name}
              onChange={(e) => setFieldValue('name', e.target.value)}
              sx={{
                input: { color: '#ffffff' },
                label: { color: '#ffffff' },
                '& .MuiOutlinedInput-root': {
                  '& fieldset': {
                    borderColor: '#ffffff',
                  },
                  '&:hover fieldset': {
                    borderColor: '#ffffff',
                  },
                  '&.Mui-focused fieldset': {
                    borderColor: '#ffffff',
                  },
                },
              }}
              error={touched.name && Boolean(errors.name)}
              helperText={touched.name && errors.name}
            />

            <FormControlLabel
              control={
                <Checkbox
                  checked={values.attending}
                  onChange={(e) => setFieldValue('attending', e.target.checked)}
                  color="primary"
                  sx={{
                    color: '#ffffff',
                    '&.Mui-checked': { color: '#ffffff' },
                  }}
                />
              }
              label="Иду на день рождения"
              sx={{ color: '#ffffff' }}
            />

            <Typography variant="h6" sx={{ marginTop: '20px', marginBottom: '10px' }}>
              Выберите алкогольные напитки:
            </Typography>

            <FormGroup>
              {['Пиво', 'Водка', 'Вино', 'Виски', 'Ром', 'Текила'].map((drink) => (
                <FormControlLabel
                  key={drink}
                  control={
                    <Checkbox
                      value={drink}
                      checked={values.drinks.includes(drink)}
                      onChange={(e) => {
                        const { value, checked } = e.target;
                        if (checked) {
                          setFieldValue('drinks', [...values.drinks, value]);
                        } else {
                          setFieldValue('drinks', values.drinks.filter((item) => item !== value));
                        }
                      }}
                      color="primary"
                      sx={{
                        color: '#ffffff',
                        '&.Mui-checked': { color: '#ffffff' },
                      }}
                    />
                  }
                  label={drink}
                  sx={{ color: '#ffffff' }}
                />
              ))}
            </FormGroup>

            <Button
              type="submit"
              variant="contained"
              color="primary"
              sx={{ marginTop: '20px', width: '100%' }}
            >
              Отправить приглашение
            </Button>
          </Form>
        )}
      </Formik>

      {alertMessage && (
        <Alert
          severity={alertMessage.includes('успешно') ? 'success' : 'error'}
          sx={{ marginTop: '20px' }}
        >
          {alertMessage}
        </Alert>
      )}
    </Box>
  );
};

export default BirthdayInvitationForm;
