import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { Box, TextField, Checkbox, FormControlLabel, Button, Typography, FormGroup } from '@mui/material';

const BirthdayInvitationForm = () => {
  const [name, setName] = useState('');
  const [attending, setAttending] = useState(false);
  const [drinks, setDrinks] = useState([]);

  const handleDrinkChange = (event) => {
    const { value, checked } = event.target;
    setDrinks(prevDrinks =>
      checked ? [...prevDrinks, value] : prevDrinks.filter(drink => drink !== value)
    );
  };

  const sendEmail = (e) => {
    e.preventDefault();

    const templateParams = {
      name: name,
      attending: attending ? 'Да' : 'Нет',
      drinks: drinks.join(', '),
    };

    emailjs.send('service_qqiw1rh', 'template_ndcfuje', templateParams, 'KCn971jYkZdaYlyjl')
      .then((response) => {
        console.log('SUCCESS!', response.status, response.text);
        // Здесь можно добавить уведомление о успешной отправке
      }, (err) => {
        console.log('FAILED...', err);
        // Здесь можно добавить уведомление об ошибке
      });
  };

  return (
    <Box
      component="form"
      onSubmit={sendEmail}
      sx={{
        maxWidth: '400px',
        margin: 'auto',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        backgroundColor: '#ffffff',
        backgroundImage: 'url(https://i.pinimg.com/originals/b3/40/64/b340640f62785c3a5b3b7554090c2186.jpg)',
        backgroundSize: 'cover', 
        backgroundPosition: 'center',
        backgroundColor: 'white',
        color: "white"
      }}
    >
      <Typography variant="h5" align="center" sx={{ marginBottom: '20px' }}>
        Приглашение на день рождения
      </Typography>

      <TextField
        fullWidth
        label="Ваше имя"
        variant="outlined"
        value={name}
        onChange={(e) => setName(e.target.value)}
        margin="normal"
        sx={{
          input: { color: '#ffffff' }, // Цвет текста внутри поля
          label: { color: '#ffffff' }, // Цвет текста метки
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#ffffff', // Цвет рамки поля ввода
            },
            '&:hover fieldset': {
              borderColor: '#ffffff', // Цвет рамки при наведении
            },
            '&.Mui-focused fieldset': {
              borderColor: '#ffffff', // Цвет рамки при фокусе
            },
          },
        }}
      />

      <FormControlLabel
        control={
          <Checkbox
            checked={attending}
            onChange={() => setAttending(!attending)}
            color="primary"
            sx={{
              color: '#ffffff', // Цвет чекбокса
              '&.Mui-checked': { color: '#ffffff' }, // Цвет чекбокса при выборе
            }}
          />
        }
        label="Иду на день рождения"
        sx={{ color: '#ffffff' }}
      />

      <Typography variant="h6" sx={{ marginTop: '20px', marginBottom: '10px' }}>
        Выберите алкогольные напитки:
      </Typography>

      <FormGroup>
        {['Пиво', 'Водка', 'Вино', 'Виски', 'Ром', 'Текила'].map((drink) => (
          <FormControlLabel
            key={drink}
            control={
              <Checkbox
                value={drink}
                onChange={handleDrinkChange}
                color="primary"
                sx={{
                  color: '#ffffff', // Цвет чекбокса
                  '&.Mui-checked': { color: '#ffffff' }, // Цвет чекбокса при выборе
                }}
              />
            }
            label={drink}
            sx={{ color: '#ffffff' }}
          />
        ))}
      </FormGroup>

      <Button
        type="submit"
        variant="contained"
        color="primary"
        sx={{ marginTop: '20px', width: '100%' }}
      >
        Отправить приглашение
      </Button>
    </Box>
  );
};

export default BirthdayInvitationForm;
